import { useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux"
import { resetUser, updateUser } from "./features/UserSlice";
import { UsersSelectors } from "./Selectors/UsersSelectors";


export default function UserForm(){
    const [showform ,setshowform]=useState(false)
    const dispatch = useDispatch()
const users = useSelector(UsersSelectors)
const name = useRef()
const age = useRef()
const country = useRef()

    const ResetUser = (e) => {

        e.preventDefault(); // Ajoutez les parenthèses pour appeler la méthode
        alert('Restusers')
        dispatch(resetUser())
    };


    const UpdateUser =(e)=>{
e.preventDefault()
setshowform(true)

    }



    const submitUser =(e)=>{
        e.preventDefault()
        setshowform(false);
      
        dispatch(updateUser({name: name.current.value,
            age: age.current.value,
            country: country.current.value,}))
    }
    
    return(
       
       <>
       <div className="mt-3">
           <button type="button" className="btn btn-primary me-2" onClick={UpdateUser}>Update</button>
           <button type="button" className="btn btn-secondary" onClick={ResetUser}>Reset</button>
         </div>  
 {showform && (   <>
                <div className="mb-3 ">
                    <label htmlFor="input1" className="form-label">Field 1</label>
                    <input 
                        type="text"
                        id="input1"
                        className="form-control"
                        placeholder="Enter first value"
                     defaultValue={users?.name}
                   ref={name}
                    />
                </div>

                <div className="mb-3">
                    <label htmlFor='input2' className='form-label'>Field 2</label>
                    <input 
                        type='text'
                        id='input2'
                        className='form-control'
                        placeholder='Enter second value'
                        defaultValue={users?.age}
                        ref={age}
                   />
               </div>

               <div className='mb-3'>
                   <label htmlFor='countrySelect' className='form-label'>Select Country</label>
                   <select 
                       id='countrySelect' 
                       name=''  
                       className= 'form-select'  
                       defaultValue={users?.ma}
                       ref={country}
                   >
                      {/* Default option */}
                      {/* Ensuring that options have meaningful text for better UX */}
                     {/* Setting default option as disabled and hidden */}
                      <option value="" disabled hidden>Choose a country...</option>          
                      <option value= 'ma'>Morocco (ma)</option>              
                     <option value= 'dz'>Algeria (dz)</option>             
                     <option value= 'es'>Spain (es)</option>
                  </select>

              </div>
              <button type="button" onClick={submitUser}  className="btn btn-primary me-2">submit</button>

               
        </> )}
       </>
    
          
    )
}
