import React, { useEffect, useState } from "react";

export const UseFetch = (url) => {
  const [data, setData] = useState(null); // Move useState here

  useEffect(() => {
    fetch(url) // https://fakestoreapi.com/products
      .then((response) => response.json()) // Extract JSON data
      .then((response) => {
        setData(response); // Set data
      })
    
  }, [url]); // Run only once, but re-run if URL changes

return data


  return (
    <>
      {data.length > 0 ? JSON.stringify(data) : "API error"}
      <div>Custom Hooks Component</div> {/* Render component */}
    </>
  );
}
