import React, { useEffect, useReducer, useState } from "react";
import { UseFetch } from "./useFetch";
import { datareducer, init_value } from "./reducer";

export default function CustomHooks() {
  const [data, dispatch] = useReducer(datareducer, init_value);
  const [searchInput, setSearchInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");

  const productData = UseFetch("https://fakestoreapi.com/products");
  const categoriesData = UseFetch("https://fakestoreapi.com/products/categories");

  useEffect(() => {
    if (productData) {
      dispatch({ type: "PRODUCT_CHANGE", product: productData });
    }
    if (categoriesData) {
      dispatch({ type: "CATEGORIES_CHANGE", categories: categoriesData });
    }
  }, [productData, categoriesData]);

  const filteredProducts = (data.product || []).filter((product) => {
    const matchesSearch =
      product.id.toString().startsWith(searchInput) ||
      product.title.toLowerCase().includes(searchInput.toLowerCase()) ||
      product.price.toString().startsWith(searchInput) ||
      product.description.toLowerCase().includes(searchInput.toLowerCase()) ||
      product.category.toLowerCase().startsWith(searchInput);

    const matchesCategory =
      !selectedCategory || product.category === selectedCategory;

    return matchesSearch && matchesCategory;
  });

  return (
    <div className="container-fluid ms-auto w-75">
      <h1>Custom Hooks</h1>

      <div className="mb-4">
        <h2>Search</h2>
        <form>
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search products..."
              onChange={(e) => setSearchInput(e.target.value)}
            />
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="">All Categories</option>
              {(data.categories || []).map((category, index) => (
                <option key={index} value={category}>
                  {category}
                </option>
              ))}
            </select>
          </div>
        </form>
      </div>

      <table className="table table-striped">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Price</th>
            <th>Description</th>
            <th>Category</th>
            <th>Image</th>
            <th>Rating</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts.map((product) => (
            <tr key={product.id}>
              <td>{product.id}</td>
              <td>{product.title}</td>
              <td>${product.price.toFixed(2)}</td>
              <td>{product.description}</td>
              <td>{product.category}</td>
              <td>
                <img
                  src={product.image}
                  alt={product.title}
                  width="50"
                  style={{ borderRadius: "5px" }}
                />
              </td>
              <td>{product.rating?.rate ?? "N/A"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
