import React, { useReducer } from "react";
import { UseFetch } from "./useFetch";
import { Default_value, product_reducer } from "./reducer";

export default function CustomHooks() {
    const [productvalue, dispatch] = useReducer(product_reducer, Default_value);

    const productData = UseFetch("https://fakestoreapi.com/products");
    dispatch({ type: "PRODUCT_CHANGE", payload: { collection: productData } });

    const categoriesData = UseFetch("https://fakestoreapi.com/products/categories");
    dispatch({ type: "CATEGORIES_CHANGE", payload: { hascategories: categoriesData } });

    return (
        <>
            <div className="container-fluid ms-auto w-75">
                <h2>search</h2>
                <form> {/* Add your search functionality here */}
                    <div className="form-group">
                        <label htmlFor="category"></label>
                        <hr />
                        <h5 className="title">category:</h5>
                        <div className="btn-group">
                            {productvalue.categories.map((category, index) => (
                                <button key={index} className="btn btn-secondary" type="button">
                                    {category}
                                </button>
                            ))}
                        </div>
                    </div>
                </form>
                <table className="table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Price</th>
                            <th>Description</th>
                            <th>Category</th>
                            <th>Image</th>
                            <th>Rating</th>
                        </tr>
                    </thead>
                    <tbody>
                        {productvalue.product.map((product) => (
                            <tr key={product.id}>
                                <td>{product.id}</td>
                                <td>{product.title}</td>
                                <td>{product.price}</td>
                                <td>{product.description}</td>
                                <td>{product.category}</td>
                                <td><img src={product.image} alt={product.title} style={{width: '50px'}}/></td> {/* Adjust image size as needed */}
                                <td>{product.rating.rate}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );
}
