import React, { useContext, useEffect, useRef, useState } from "react";
import { UsersContext } from "./apk";
import { useNavigate, useParams } from "react-router-dom";

export default function Update({updateuser}){
    const [currentuser ,setcurrentuser]=useState({})
    const params= useParams()
    const context = useContext(UsersContext);
const navigate = useNavigate()
    const fullname=useRef()
    const country=useRef()


    //recuperer dtat dans input
    useEffect(() => {
      const {id} = params
    const user =  context.usersd.filter((user)=>user.id==id)
   console.log("test context",context)
    if(user.length>0){
        setcurrentuser(...user)
    }
    else{
        console.log("not faunde")
    }
  
    }, [params,context]);

    //trensferer data à db

    const handlesubmit = (e) => {
        e.preventDefault();
       
        const fullnamedata = fullname.current.value;
        const countrydata = country.current.value;
        const {id} = params
        const changeUser = { fullname: fullnamedata, country: countrydata ,id:id };
    console.log("testing data de update dans form",changeUser)
        context.updateuser(changeUser);
        navigate()
      
    };
    return(
        <>
          <form action="" onSubmit={handlesubmit} className="container mt-4 " >
           <div className="mb-3"> <label htmlFor=""  className="form-label">id</label>
             <input type="text" readOnly name="" id=""  className="form-control" defaultValue={currentuser?.id} />
             </div>
             <div className="mb-3">
            <label htmlFor="" className="form-label">fullname</label>
            <input type="text" name="fullname" id="fullname" className="form-control"  defaultValue={currentuser?.fullname} ref={fullname}  />
            </div>
            <div className="mb-3">
            <label htmlFor="" className="form-label">country</label>
            <select name="country" id="country"  className="form-select"  defaultValue={currentuser?.country} ref={country} >
                <option value="maroc">maroc</option>
                <option value="frence">frence</option>
                <option value="espane">espane</option>
                <option value="usa">usa</option>
            </select>
            </div>
            
            <button type="submit" className="btn btn-primary">submit</button>
        </form>
        </>
    )
}
