import React, { useEffect, useReducer, useState } from "react";
import { UseFetch } from "./useFetch";
import { datareducer, init_value } from "./reducer";

export default function CustomHooks() {
  const [data, dispatch] = useReducer(datareducer, init_value);
  const [searchInput, setSearchInput] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");

  const productFetch = UseFetch("https://fakestoreapi.com/products");
  const categoriesFetch = UseFetch("https://fakestoreapi.com/products/categories");

  useEffect(() => {
    if (productFetch && productFetch.data) {
      dispatch({ type: "PRODUCT_CHANGE", product: productFetch.data });
    }
    if (categoriesFetch && categoriesFetch.data) {
      dispatch({ type: "CATEGORIES_CHANGE", categories: categoriesFetch.data });
    }
  }, [productFetch, categoriesFetch]);

  const filteredProducts = (data.products || []).filter(
    (product) =>
      (product.id.toString().startsWith(searchInput) ||
        product.title.toLowerCase().includes(searchInput.toLowerCase()) ||
        product.price.toString().startsWith(searchInput) ||
        product.description.toLowerCase().includes(searchInput.toLowerCase()) ||
        product.category.toLowerCase().startsWith(searchInput)) &&
      product.category.toString().includes(selectedCategory)
  );

  return (
    <>
      <h1>Custom Hooks</h1>

      <div className="container-fluid ms-auto w-75">
        <h2>Search</h2>
        <form>
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search products..."
              onChange={(e) => setSearchInput(e.target.value)}
            />

            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="">All Categories</option>
              {(data.categories || []).map((category, index) => (
                <option key={index} value={category}>
                  {category}
                </option>
              ))}
            </select>
          </div>
        </form>
        {productFetch.loading || categoriesFetch.loading ? (
          <p>Loading...</p>
        ) : productFetch.error || categoriesFetch.error ? (
          <p>Error loading data</p>
        ) : (
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Title</th>
                <th>Price</th>
                <th>Description</th>
                <th>Category</th>
                <th>Image</th>
                <th>Rating</th>
              </tr>
            </thead>
            <tbody>
              {filteredProducts.map((product) => (
                <tr key={product.id}>
                  <td>{product.id}</td>
                  <td>{product.title}</td>
                  <td>${product.price}</td>
                  <td>{product.description}</td>
                  <td>{product.category}</td>
                  <td>
                    <img src={product.image} alt={product.title} width="50" />
                  </td>
                  <td>{product.rating?.rate}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </>
  );
}
