import React, { Component } from "react";
import HellowAli from "./hellowAli";

export default class Test8 extends Component {
  constructor() {
    super();
    this.state = {
      displayname: true,
    };
  }

  // Fonction déclenchée lors du clic sur le body
  clickbody = () => {
    alert("Boom!!");
  };

  // Ajoute un écouteur d'événement lorsqu'on monte le composant
  componentDidMount() {
    document.addEventListener("click", this.clickbody);
  }

  // Fonction pour basculer l'état de displayname
  toggleName = (e) => {
    e.stopPropagation(); // Empêche la propagation du clic au document
    this.setState((prevState) => {
      return { displayname: !prevState.displayname };
    });
  };

  // Supprime l'écouteur d'événement lors du démontage du composant
  componentWillUnmount() {
    console.log("Le composant est démonté.");
    document.removeEventListener("click", this.clickbody);
  }

  render() {
    return (
      <>
        {/* Affiche l'état de displayname */}
        {this.state.displayname.toString()}

        {/* Affiche le bouton pour basculer l'affichage de HellowAli */}
        {this.state.displayname && (
          <button onClick={this.toggleName}>Click</button>
        )}

        {/* Affiche HellowAli seulement si displayname est true */}
        {this.state.displayname && <HellowAli />}
      </>
    );
  }
}
