import React, { useContext, useEffect, useState } from "react";
import { UsersContext } from "./apk";
import { useParams } from "react-router-dom";

export default function Update(){
    const [currentuser ,setcurrentuser]=useState()
    const params= useParams()
    const context = useContext(UsersContext);

    cons

    //recuperer dtat dans input
    useEffect(() => {
      const {id} = params
    const user =  context.usersd.filter((user)=>user.id==id)
   
    if(user.length>0){
        setcurrentuser(...user)
    }
    else{
        console.log("not faunde")
    }
  
    }, [params,context]);

    //trensferer data à db

    const handlesubmit = (e) => {
        e.preventDefault();
       
        const fullnamedata = fullname.current.value;
        const countrydata = country.current.value;
        const iddata =  context.lastid + 1 ;
        const newUser = { fullname: fullnamedata, country: countrydata, id: iddata, };
    
        context.onaddusers(newUser);
        
      
    };
    return(
        <>
          <form action=""  className="container mt-4 " >
           <div className="mb-3"> <label htmlFor=""  className="form-label">id</label>
             <input type="text" readOnly name="" id=""  className="form-control" defaultValue={currentuser?.id} />
             </div>
             <div className="mb-3">
            <label htmlFor="" className="form-label">fullname</label>
            <input type="text" name="fullname" id="fullname" className="form-control"  defaultValue={currentuser?.fullname}  />
            </div>
            <div className="mb-3">
            <label htmlFor="" className="form-label">country</label>
            <select name="country" id="country"  className="form-select"  defaultValue={currentuser?.country}>
                <option value="maroc">maroc</option>
                <option value="frence">frence</option>
                <option value="espane">espane</option>
                <option value="usa">usa</option>
            </select>
            </div>
            
            <button type="submit" className="btn btn-primary">submit</button>
        </form>
        </>
    )
}
