import React, { useReducer, useRef, useState } from "react";
import { form_reducer, form_reducer_default_value } from "./form_reducer";

export default function UseReducer() {
    const firstnameRef =useRef({})
    const lastenameRef =useRef({})
    const checkboxRef =useRef({})
    const [formvaluewithreducer , dispatch]=useReducer(form_reducer,form_reducer_default_value)
    const [formvalue , setformvalue]=(useState({form_reducer_default_value}))
    const [submittedData, setSubmittedData] = useState(null);



    const displaysubmit=(e)=>{
        dispatch({type:"INPUT_CHANGE" ,
            payload: firstnameRef.current })


            dispatch({type:"INPUT_CHANGE" ,
                payload: lastenameRef.current })


                dispatch({
                    type: "checkboxRef",
                    payload: checkboxRef.current,
                });
                

                    
            dispatch({type:"SELECT_CHANGE" ,
                payload: firstnameRef.current })


        const currentTarget = e.currentTarget
        const id= currentTarget.id 
        console.log("ffffffffff"  , id)  
        let value = undefined     
        switch(currentTarget.type) {
     case 'text':
        value=currentTarget.value
        break ; 
        case "checkbox" :
            value = currentTarget.checked

        }          
        
        setformvalue((preventstate)=>{
            return {...preventstate ,  ...{[id]:value} }
           
        })
        console.log("ddddd",formvalue)
        
    }

    const handleSubmit=(e)=>{
        e.preventDefault()
        
    }
    return(
        <>
        {JSON.stringify(formvalue)}
        <div class="form-group">
              
      <form action=""   onSubmit={handleSubmit } >

      <div class="form-group">
      <label for="">firstname</label>
<input type="text" ref={firstnameRef} name="firstname" id="firstname" onChange={displaysubmit}/> 
</div>
<div class="form-group">
<label for="">lastename</label>
<input type="text" name="lastename" ref={lastenameRef} id="lastename" onChange={displaysubmit}/>
</div> 

<div class="form-group">

<input type="checkbox" name="checkbox" id="checkbox" ref={checkboxRef} onChange={displaysubmit} /> 
</div>

      </form>
  <button type="submit" name="submit" id="submit" > click</button>
        </div>
       


<table class="table">
    <thead>
        <tr>
            <th>firstname</th>
            <th>lastename</th>
            <th>ans</th>
            <th>checkbox</th>
        </tr>
    </thead>
    <tbody>
        <tr>
            <td scope="row"> {JSON.stringify(formvalue.firstname)}</td>
            <td>{JSON.stringify(formvalue.lastename)}</td>
            <td>{JSON.stringify(formvalue.ans)}</td>
            <td>{JSON.stringify(formvalue.checkbox)}</td>
        </tr>
        
    </tbody>
</table>

</>
    )
}
