import React, { createContext, useContext, useEffect, useRef } from "react";
import { UsersContext } from "./apk";
import { useNavigate } from "react-router-dom";  // Ajoutez ceci pour la redirection

export default function Create() {
  const fullname = useRef();
  const country = useRef();
  const navigate = useNavigate();  // Utilisation de useNavigate pour la redirection

  const context = useContext(UsersContext);

  useEffect(() => {
    console.log("Testing de context", context.lastid);
  }, [context]);

  const handlesubmit = (e) => {
    e.preventDefault();
    
    const fullnamedata = fullname.current.value;
    const countrydata = country.current.value;
    const iddata = context.lastid + 1;
    const newUser = { fullname: fullnamedata, country: countrydata, id: iddata };

    // Ajouter le nouvel utilisateur à usersd
    context.onaddusers(newUser);
    
    // Rediriger vers la page des utilisateurs après l'ajout
    navigate('/users/Read');
  };

  return (
    <>
      <form action="" onSubmit={handlesubmit} className="container mt-4">
        <div className="mb-3">
          <label htmlFor="" className="form-label">id</label>
          <input
            type="text"
            readOnly
            name=""
            id=""
            value={context.lastid + 1}
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="" className="form-label">fullname</label>
          <input
            type="text"
            name="fullname"
            id="fullname"
            ref={fullname}
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="" className="form-label">country</label>
          <select
            name="country"
            id="country"
            ref={country}
            className="form-select"
          >
            <option value="maroc">Maroc</option>
            <option value="france">France</option>
            <option value="espagne">Espagne</option>
            <option value="usa">USA</option>
          </select>
        </div>
        
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </>
  );
}
