import React, { useEffect, useState } from "react";
import Production from "./production";

export default function Test10() {
  const [productliste, setProductliste] = useState([]);
  const [searchinput, setSearchinput] = useState("");
  const [categoryliste, setCategoryliste] = useState([]); // Toujours un tableau
  const [getcategoryliste, setGetcategoryliste] = useState("");

  // Fonction pour récupérer les produits
  const product = () => {
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json())
      .then((response) => setProductliste(response));
  };

  // Fonction pour récupérer les catégories
  const getcategory = () => {
    fetch("https://fakestoreapi.com/products/categories")
      .then((response) => response.json())
      .then((response) => setCategoryliste(response));
  };

  // Appel des API au montage du composant
  useEffect(() => {
    product();
    getcategory();
  }, []);

  // Afficher les boutons des catégories
  const displaycategorys = () => {
    if (categoryliste.length > 0) {
      return categoryliste.map((category) => (
        <button
          className="btn btn-secondary"
          key={category}
          onClick={() => setGetcategoryliste(category)} // Met à jour la catégorie sélectionnée
        >
          {category}
        </button>
      ));
    }
    return null;
  };

  // Filtrer et afficher les produits
  const data = () => {
    const filteredProducts = productliste.filter((product) => {
      // Si une catégorie est sélectionnée, on filtre par cette catégorie
      const categoryMatch =
        getcategoryliste === "" || product.category === getcategoryliste;

      // Filtrer par recherche ou par catégorie
      return (
        categoryMatch &&
        (product.title.toLowerCase().includes(searchinput.toLowerCase()) ||
          product.price.toString().startsWith(searchinput) ||
          product.description.toLowerCase().includes(searchinput.toLowerCase()) ||
          product.category.toLowerCase().startsWith(searchinput.toLowerCase()) ||
          product.id.toString().startsWith(searchinput))
      );
    });

    return filteredProducts.map((product, key) => (
      <Production productdata={product} key={key} />
    ));
  };

  // Gérer la recherche
  const search = (e) => {
    e.preventDefault();
    const searchdata = document.querySelector("#search").value;
    setSearchinput(searchdata);
  };

  return (
    <div className="container-fluid ms-auto w-75">
      <h2>Search</h2>
      <form onSubmit={search}>
        <div className="form-group">
          <label htmlFor="search">Search:</label>
          <input type="text" name="search" id="search" />
          <input type="submit" name="submit" id="submit" value="Search" />
          <hr />
          <h5 className="title">Category:</h5>
          <div className="btn-group">{displaycategorys()}</div>
        </div>
      </form>

      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Price</th>
            <th>Description</th>
            <th>Category</th>
            <th>Image</th>
            <th>Rating</th>
          </tr>
        </thead>
        <tbody>{data()}</tbody>
      </table>
    </div>
  );
}
