import { type } from "@testing-library/user-event/dist/type"
import { connect } from "react-redux"

export default function Counter({counter,onIncrement, onDecrement}){

    const Increment =()=>{
        console.log("onIncrement" , onIncrement())
    }
    const Decrement =()=>{
        onDecrement()
    }
    return(
        <>
        <div>
            <span>{counter}</span>
            <button type="button" className="btn btn-primary" onClick={Increment}>Increment</button>
            <button type="button" className="btn btn-primary" onClick={Decrement}>Decrement</button>
        </div>
        </>
    )
}



const mapStateToProps = (state) => ({
    counter: state.value,
  });
  
  const mapDispatchToProps = (dispatch) => ({
    onIncrement: (value) => dispatch({ type: "counter/increment", payload: {value} }),
    onDecrement: (value) => dispatch({ type: "counter/decrement", payload: {value} }),
  });
  
  // Connect the Counter component to Redux and export it 
  export const CounterStore = connect(mapStateToProps, mapDispatchToProps)(Counter);
