import React, { useCallback, useState, useEffect } from "react";

export default function UseCallbackExample() {
    const [isRendered, setIsRendered] = useState(false);

    const a = 2;
    const b = 2;

    // دالة محسّنة باستخدام useCallback
    const calculateSum = useCallback(() => {
        const sum = a + b;
        console.log("Sum calculated:", sum);
        return sum;
    }, [a, b]);

    // دالة عادية
    const logMessage = () => {
        console.log("This is not using useCallback.");
    };

    // لتحديث الحالة
    const triggerRender = () => {
        setIsRendered(true);
        console.log("Rendered state set to true.");
    };

    // تنفيذ logMessage عندما تتغير isRendered
    useEffect(() => {
        if (isRendered) {
            logMessage();
        }
    }, [isRendered]);

    return (
        <>
            <input
                type="button"
                value="Calculate Sum"
                onClick={calculateSum}
            />

            <input
                type="button"
                value="Log Message"
                onClick={logMessage}
            />

            <input
                type="button"
                value="Trigger Render"
                onClick={triggerRender}
            />

            {/* عرض النص لتأكيد التغيير في الحالة */}
            {isRendered && <p>The component has been rendered!</p>}
        </>
    );
}
