import { createStore } from 'redux';
import { Counter } from './counter';



const store = createStore(Counter);
store.subscribe(()=>{
    alert("state changed !!!") 
})
store.dispatch({ type:'counter/increment'})
store.dispatch({ type:'counter/dicriment'})
