import React, { useEffect, useRef, useState } from "react";


export default function Create({lastid,onaddusers}){

const fullname=useRef()
const country= useRef()




const handlesubmit = (e) => {
    e.preventDefault();
   
    const fullnamedata = fullname.current.value;
    const countrydata = country.current.value;
    const iddata = lastid + 1 ;
    const newUser = { fullname: fullnamedata, country: countrydata, id: iddata, };

    onaddusers(newUser);

  
};

    return(
        <>
        <form onSubmit={handlesubmit} class="container mt-4">
    <div class="mb-3">
        <label for="id" class="form-label">ID</label>
        <input 
            type="text" 
            class="form-control" 
            id="id" 
            name="id" 
            value={lastid + 1} 
            readOnly 
        />
    </div>
    <div class="mb-3">
        <label for="fullname" class="form-label">Full Name</label>
        <input 
            type="text" 
            class="form-control" 
            id="fullname" 
            name="fullname" 
            ref={fullname} 
            placeholder="Enter your full name" 
        />
    </div>
    <div class="mb-3">
        <label for="country" class="form-label">Country</label>
        <select 
            class="form-select" 
            id="country" 
            name="country" 
            ref={country}
        >
            <option value="maroc">Morocco</option>
            <option value="frence">France</option>
            <option value="espane">Spain</option>
            <option value="usa">USA</option>
        </select>
    </div>
    <button type="submit" class="btn btn-primary">Submit</button>
</form>

        </>
    )
}
