import Production from "./production";


export default function Test10(){

    const [productliste  , setproductliste]=useState([])
    const [searchinput  , setsearchinput]=useState("")
    
  const  product=()=>{
    fetch("https://fakestoreapi.com/products")
    .then((response) => response.json()) 
        .then((response)=>setproductliste(response)  )

    }
    useEffect(()=>{
        product()
      
    },[])                           
    
    const data=()=>{
        const filteredProducts = productliste.filter((product) =>
            product.title.toLowerCase().startsWith(searchinput.toLowerCase()))
        
       return productliste.map(   (preventstate,key) =>{
            console.log(preventstate )
           return <Production productdata={preventstate} key={key}/>

        })
    }

 

    const search =(e)=>{
e.preventDefault()
const searchdata= document.querySelector("#search").value
setsearchinput(searchdata)

if(productliste){
    const productemp = productliste.filter(product =>{
        console.log("mmmm",product.id)
        return    product.title.toLowerCase().startsWith(searchinput.toLowerCase())
    } 
       
    )
   
}
    }
    return(
        <div className="container-fluid ms-auto w-75">
            <h2>search</h2>
            <form >
                <div class="form-group">
                  <label for=""></label>
                 <input type="text" name="search" id="search" />
                  
                </div>
                <input type="submit" name="" id="" value="search" onClick={search} />
            </form>
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Price</th>
              <th>Description</th>
              <th>Category</th>
              <th>Image</th>
              <th>Rating</th>
            </tr>
          </thead>
          <tbody>
       {data()}
          </tbody>
        </table>
       
      
      </div>
    )
}
