import React, { useRef } from "react";

export default function Test14() {
  const name = useRef();
  const email = useRef();
  const message = useRef();
  const country = useRef();
  const checkbox = useRef();

  const formvalide = (e) => {
    e.preventDefault();

    // Accès aux valeurs des champs
    const nameValue = name.current.value;
    const emailValue = email.current.value;
    const messageValue = message.current.value;
    const countryValue = country.current.value;
    const checkboxChecked = checkbox.current.checked;

    // Affichage dans la console
    console.log("Name:", nameValue);
    console.log("Email:", emailValue);
    console.log("Message:", messageValue);
    console.log("Country:", countryValue);
    console.log("Checkbox:", checkboxChecked);

    // Validation
    if (nameValue === "") {
      alert("Le champ 'Nom' est requis.");
    }

    if (emailValue === "") {
      alert("Le champ 'Email' est requis.");
    }

    if (messageValue === "") {
      alert("Le champ 'Message' est requis.");
    }

    if (!checkboxChecked) {
      alert("Veuillez accepter les conditions.");
    }
  };

  return (
    <div className="form-group">
      <form onSubmit={formvalide}>
        <input type="text" name="name" id="name" ref={name} placeholder="Nom" />
        <input type="email" name="email" id="email" ref={email} placeholder="Email" />
        <textarea name="message" id="message" ref={message} placeholder="Message" />
        <select name="country" id="country" ref={country}>
          <option value="Maroc">Maroc</option>
          <option value="Algery">Algery</option>
          <option value="egybest">Egybest</option>
        </select>
        <div>
          <input type="checkbox" name="checkbox" id="checkbox" ref={checkbox} />
          <label htmlFor="checkbox">Accepter les conditions</label>
        </div>
        <input type="submit" name="submit" value="Envoyer" />
      </form>
    </div>
  );
}
