import React, { useEffect, useState } from "react";
import { UseFetch } from "./useFetch";
import Production from "../partie1/p10/production";

export default function CustomHooks() {
    const [productList, setProductList] = useState([]);
    const [searchInput, setSearchInput] = useState("");
    const [categoryList, setCategoryList] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState("");

    // Utiliser les hooks personnalisés pour récupérer les produits et les catégories
    const productsData = UseFetch("https://fakestoreapi.com/products");
    const categoriesData = UseFetch("https://fakestoreapi.com/products/categories");

    useEffect(() => {
        if (productsData) {
            setProductList(productsData);
        }
        if (categoriesData) {
            setCategoryList(categoriesData);
        }
    }, [productsData, categoriesData]);

    const displayCategories = () => {
        if (categoryList.length > 0) {
            return categoryList.map((category, index) => (
                <button
                    key={index}
                    className="btn btn-secondary"
                    name="category"
                    id="category"
                    onClick={() => setSelectedCategory(category)}
                >
                    {category}
                </button>
            ));
        }
        return null;
    };

    const filterData = () => {
        const filteredProducts = productList.filter((product) =>
            (product.id.toString().startsWith(searchInput) ||
                product.title.toLowerCase().includes(searchInput.toLowerCase()) ||
                product.price.toString().startsWith(searchInput) ||
                product.description.toLowerCase().includes(searchInput.toLowerCase()) ||
                product.category.toLowerCase().startsWith(searchInput.toLowerCase())) &&
            (selectedCategory ? product.category === selectedCategory : true)
        );

        return filteredProducts.map((product, index) => (
            <Production productdata={product} key={index} />
        ));
    };

    const handleSearch = (e) => {
        e.preventDefault();
        const searchData = document.querySelector("#search").value;
        setSearchInput(searchData);
    };

    return (
        <div className="container-fluid ms-auto w-75">
            <h2>Search</h2>
            <form onSubmit={handleSearch}>
                <div className="form-group">
                    <label htmlFor="search">Search Products:</label>
                    <input type="text" name="search" id="search" className="form-control" />
                    <input type="submit" name="submit" id="submit" className="btn btn-primary mt-2" />
                    <hr />
                    <h5 className="title">Category:</h5>
                    <div className="btn-group">{displayCategories()}</div>
                </div>
            </form>
            <table className="table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Price</th>
                        <th>Description</th>
                        <th>Category</th>
                        <th>Image</th>
                        <th>Rating</th>
                    </tr>
                </thead>
                <tbody>{filterData()}</tbody>
            </table>
        </div>
    );
}
