import React, { useEffect, useState } from "react";

export default function CustomHooks() {
  const [data, setData] = useState([]); // Initialisation du state

  useEffect(() => {
    // Utilisation correcte de `fetch` pour obtenir les données
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json()) // Extraction des données JSON
      .then((response) => {
        setData(response); // Mise à jour du state avec les données
      })
      .catch((error) => {
        console.error("Erreur lors de la récupération des données :", error);
      });
  }, []); // Exécution une seule fois

  return (
    <div>
      {/* Rendu des données si elles existent */}
      {data.length > 0 ? (
        data.map((item) => (
          <div key={item.id}>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <p>Price: ${item.price}</p>
          </div>
        ))
      ) : (
        <p>Chargement des données...</p> // Message pendant le chargement
      )}
      <div>Custom Hooks Component</div>
    </div>
  );
}
