import React, { useMemo, useState } from "react";

// Fonction qui simule un calcul coûteux
const expensivecalcul = (value) => {
    console.log("Calcul en cours...");
    for (let i = 0; i < 10000; i++) {
        value++;
    }
    return value; // Retourne la valeur finale après les calculs
};

export default function UseMemoComponent() {
    const [count, setCount] = useState(1); // Valeur initiale

    // Utilisation de useMemo pour mémoïser le calcul coûteux
    const memoizedValue = useMemo(() => {
        console.log("Calcul mémoïsé...");
        return expensivecalcul(count);
    }, [count]);

    const handleClick = () => {
        const dateinit = performance.now(); // Heure de début du calcul
        const findate = performance.now(); // Heure de fin
        alert(
            `Le calcul a pris ${(findate - dateinit).toFixed(2)} ms et la valeur finale est ${memoizedValue}`
        );
    };

    return (
        <>
            <input
                type="submit"
                value="Click"
                onClick={handleClick}
            />
            <button onClick={() => setCount((prev) => prev + 1)}>
                Augmenter la valeur ({count})
            </button>
        </>
    );
}
