export const initial_value = {
    productlist: [],
    error: undefined,
    isloading: false,
  };
  
  export const product_reducer = (state, action) => {
    switch (action.type) {
      case "PRODUCTLIST_CHANGE":
        return { ...state, productlist: action.payload.collection };
      case "ERROR_CHANGE":
        return { ...state, error: action.payload.haserror };
      case "LOADING_CHANGE":
        return { ...state, isloading: action.payload.isloading };
      default:
        return state;
    }
  };
