import React, { useEffect, useState, useContext } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { UsersContext } from "./apk"; // Assurez-vous que vous avez bien accès à votre context

export default function Delete() {
  const { id } = useParams();
  const context = useContext(UsersContext);
  const [deleteuser, setDeleteUser] = useState(null);
  const navigate = useNavigate(); // Pour naviguer après la suppression

  useEffect(() => {
    // Chercher l'utilisateur par ID dans le context
    const user = context.usersd.find((user) => user.id === parseInt(id));

    if (user) {
      setDeleteUser(user);
    } else {
      console.log("User not found");
    }
  }, [id, context]);

  const handleDelete = () => {
    // Supprimer l'utilisateur de la liste
    const updatedUsers = context.usersd.filter((user) => user.id !== parseInt(id));
    
    // Mettre à jour le context
    context.onaddusers(updatedUsers);
    
    // Naviguer vers la liste des utilisateurs après la suppression
    navigate("/users/Read");
  };

  return (
    <>
      {deleteuser ? (
        <div>
          <h2>Are you sure you want to delete {deleteuser.fullname}?</h2>
          <button onClick={handleDelete} className="btn btn-danger">Delete</button>
          <button onClick={() => navigate("/users/Read")} className="btn btn-secondary">Cancel</button>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </>
  );
}
