import { useEffect, useRef } from "react";

export default function Create({ lastid, onaddusers }) {
    const fullname = useRef();
    const country = useRef();
  useEffect(() => {
    let  iddata
    return () => {
        const iddata = lastid + 1;
    };
  }, [lastid]);
    const handlesubmit = (e) => {
      e.preventDefault();
  
      const fullnamedata = fullname.current.value;
      const countrydata = country.current.value;
     
  
      const newUser = {
        fullname: fullnamedata,
        country: countrydata,
        id: iddata,
      };
  
      onaddusers((prev) => [...prev, newUser]); // Met à jour les utilisateurs dans le parent
    };
  
    return (
      <>
        <form onSubmit={handlesubmit}>
          <label htmlFor="id">id</label>
          <input type="text" readOnly value={lastid + 1} />
          <label htmlFor="fullname">fullname</label>
          <input type="text" id="fullname" ref={fullname} />
          <label htmlFor="country">country</label>
          <select id="country" ref={country}>
            <option value="maroc">maroc</option>
            <option value="frence">frence</option>
            <option value="espane">espane</option>
            <option value="usa">usa</option>
          </select>
          <button type="submit">submit</button>
        </form>
      </>
    );
  }
