import React, { useContext, useEffect, useState } from "react";
import { UsersContext } from "./apk";
import { useParams } from "react-router-dom";

export default function Update(){
    const [currentuser ,setcurrentuser]=useState()
    const params= useParams()
    const context = useContext(UsersContext);
    useEffect(() => {
      const {id} = params
    const user =  context.usersd.filter((user)=>user.id==id)
    console.log("testing de context de update",user);
    setcurrentuser(...user)
    console.log("testing de context de update",currentuser);
    }, [context]);
    return(
        <>
          <form action=""  className="container mt-4 " >
           <div className="mb-3"> <label htmlFor=""  className="form-label">id</label>
             <input type="text" readOnly name="" id=""  className="form-control" />
             </div>
             <div className="mb-3">
            <label htmlFor="" className="form-label">fullname</label>
            <input type="text" name="fullname" id="fullname" className="form-control" defaultValue={currentuser.fullname}/>
            </div>
            <div className="mb-3">
            <label htmlFor="" className="form-label">country</label>
            <select name="country" id="country"  className="form-select" defaultValue={currentuser.country}>
                <option value="maroc">maroc</option>
                <option value="frence">frence</option>
                <option value="espane">espane</option>
                <option value="usa">usa</option>
            </select>
            </div>
            
            <button type="submit" className="btn btn-primary">submit</button>
        </form>
        </>
    )
}
