import React, { useEffect, useState, useRef } from "react";

export default function Test9() {
    const [count, setCount] = useState(new Date());
    const [age, setAge] = useState(0);
    
    const timerRef = useRef(null); // Utilisation d'un ref pour stocker le timer

    useEffect(() => {
        console.log("componentDidmount");
    }, []);

    useEffect(() => {
        console.log("componentDidrender");
    });

    useEffect(() => {
        if (!timerRef.current) {
            timerRef.current = setInterval(() => {
                console.log("componentDidUpdate");
                setCount(new Date());
            }, 900);
        }

        // Fonction de nettoyage pour arrêter l'intervalle quand le composant se démonte
        return () => {
            if (timerRef.current) {
                clearInterval(timerRef.current);
                console.log("Timer cleared");
                timerRef.current = null; // Remise à zéro de la référence du timer
            }
        };
    }, [age]);

    return (
        <>
            <p>Âge: {age}</p>
            <p>Date: {count.toString()}</p>
            <button
                onClick={() => {
                    setAge((prevState) => prevState + 1);
                    // Arrête le timer quand on clique
                    if (timerRef.current) {
                        clearInterval(timerRef.current);
                        console.log("Timer stopped on click");
                        timerRef.current = null; // Réinitialisation après l'arrêt
                    }
                }}
            >
                Click to stop timer
            </button>
        </>
    );
}
