import React from 'react';
import { motion } from 'framer-motion';

export default function HeroBanner() {
    return (
        <>
        <motion.div
            initial={{ x: '-100vw' }} // Départ hors de l'écran à gauche
            animate={{ x: 300 }} // Se déplace à la position d'origine
            transition={{
                duration: 1,
            }}
        >
            <motion.h2
                className="display-5 fw-normal"
                initial={{
                    opacity: 0.2,
                    color: 'darkgreen',
                }}
                animate={{
                    opacity: 1,
                    scale: 1.3,
                    color: 'darkgoldenrod',
                    textShadow: '1px 1px 1px black',
                }}
                transition={{
                    duration: 0.5,
                    delay: 1,
                }}
            >
                Bienvenue to website
            </motion.h2>
            <motion.p
                animate={{
                    scale: 1.3,
                    fontWeight: 'bold',
                }}
                transition={{
                    duration: 0.5,
                    delay: 1,
                }}
            >
                Luffy, le héros de One Piece, possède des pouvoirs élastiques grâce au Fruit Gomu Gomu no Mi,<br/>
                rendant son animation dynamique et exagérée. Ses mouvements reflètent son énergie débordante <br/>
                et sa détermination inébranlable.
            </motion.p>
        </motion.div>
        </>
    );
}
