// Types d'actions
export const Counter_Increment = "counter/increment";
export const Counter_Decrement = "counter/decrement"; // Corrigé
export const Counter_Reset = "counter/reset";

// Reducer
export const CounterRedux = (state = { value: 0 }, action) => {
  switch (action.type) {
    case Counter_Increment:
      return { value: state.value + 1 }; // Ajoute 1
    case Counter_Decrement:
      return { value: state.value - 1 }; // Soustrait 1
    case Counter_Reset:
      return { value: action.payload.value }; // Réinitialise à la valeur définie
    default:
      return state;
  }
};
