import React, { useEffect, useState } from "react";
import Production from "./production";

export default function Test10() {
  const [productliste, setproductliste] = useState([]);
  const [searchinput, setsearchinput] = useState("");

  const fetchProducts = () => {
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json())
      .then((response) => setproductliste(response));
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const data = () => {
    // On filtre les produits ici en fonction de l'input de recherche
    const filteredProducts = productliste.filter((product) =>
      product.title.toLowerCase().startsWith(searchinput.toLowerCase()) ||
    product.id.toString().startsWith(searchinput)
    );

    // On affiche les produits filtrés
    return filteredProducts.map((product, key) => (
      <Production productdata={product} key={key} />
    ));
  };

  const search = (e) => {
    e.preventDefault(); // Empêche le rechargement de la page
    const searchdata = document.querySelector("#search").value;
    setsearchinput(searchdata); // On met à jour l'input de recherche
  };

  return (
    <div className="container-fluid ms-auto w-75">
      <h2>Search</h2>
      <form onSubmit={search}>
        <div className="form-group">
          <label htmlFor="search">Search Products:</label>
          <input
            type="text"
            name="search"
            id="search"
            className="form-control"
            placeholder="Search by title..."
          />
        </div>
        <input
          type="submit"
          name="submit"
          id="submit"
          value="Search"
          className="btn btn-primary mt-2"
        />
      </form>

      <table className="table mt-4">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Price</th>
            <th>Description</th>
            <th>Category</th>
            <th>Image</th>
            <th>Rating</th>
          </tr>
        </thead>
        <tbody>{data()}</tbody>
      </table>
    </div>
  );
}
