export const init_value = {
    products: {},
    categories: {}
};

export const datareducer = (state = init_value, action) => {
    switch(action.type) {
       
        case "PRODUCT_CHANGE": 
        return{
          ...state , products:action.payload.product
        }
        case "CATEGORIES_CHANGE": 
        return{
          ...state , categories:action.payload.categories
        }
        default:
            return state;
    }
};
