import React from 'react';
import { motion } from 'framer-motion';

export default function HeroBanner() {
    return (
        <div className="text-center py-5">
            <motion.h2 // Changed h1 to h2 for smaller size
                className="display-5 fw-normal" // Reduced display size
                initial={{
                    opacity: 0.2,
                    color: 'darkgreen',
                }}
                animate={{
                    opacity: 1,
                    scale: 1.3,
                    color: 'darkgoldenrod',
                    textShadow: '1px 1px 1px black',
                }}
              
            >
                Bienvenue to website
            </motion.h2>
            <motion.p 
              animate={{
                opacity: 1,
                scale: 1.3,
                color: 'darkgoldenrod',
                textShadow: '1px 1px 1px black',
            }}
            > 
                Luffy, le héros de One Piece, possède des pouvoirs élastiques grâce au Fruit Gomu Gomu no Mi,<br/>
              rendant son animation dynamique et exagérée. Ses mouvements reflètent son énergie débordante <br/>
              et sa détermination inébranlable.
            </motion.p>
        </div>
    );
}
