import React, { useEffect, useState } from "react";
import { UseFetch } from "./useFetch";

export default function CustomHooks() {
    const [productliste  , setproductliste]=useState([])
    const [searchinput  , setsearchinput]=useState("")
    const [categoryliste  , setcategoryliste]=useState("")
    const [getcategoryliste  , setgetcategoryliste]=useState("")

  const product = UseFetch("https://fakestoreapi.com/products");
  const categoriesData = UseFetch("https://fakestoreapi.com/products/categories");

  useEffect(()=>{
    product()
    getcategory()
  
},[])        

const displaycategorys=()=>{
    console.log("dddddd" , categoryliste)
    if(categoryliste){
        return  categoryliste.map((category)=>{
        return  <button className="btn btn-secondary" name="category" id="category"  onClick={()=>  setgetcategoryliste(category)} >
            {category}
            
          </button>
        })
    }
    
   
}

const data=()=>{
   
   

console.log('gggg' , getcategoryliste)
const filteredProducts = productliste.filter((product) =>
     
(product.id.toString().startsWith(searchinput)
||  product.title.toString().includes(searchinput)
||product.price.toString().startsWith(searchinput)
||product.description.toString().includes(searchinput)
||product.category.toString().startsWith(searchinput))
&&(product.category.toString().includes(getcategoryliste))


)


   return filteredProducts.map(   (preventstate,key) =>{
      
       return <Production productdata={preventstate} key={key}/>

    })
}


const search =(e)=>{
    e.preventDefault();
const searchdata= document.querySelector("#search").value
setsearchinput(searchdata)

}
return(
    <div className="container-fluid ms-auto w-75">
        <h2>search</h2>
        <form onSubmit={search}>
            <div class="form-group">
              <label for=""></label>
             <input type="text" name="search" id="search" />
             <input type="submit" name="submit" id="submit" onClick={search}/>
              <hr />
              <h5 className="title"> category :</h5>
            <div className="btn group">
           
                 {displaycategorys()}
           
            </div>
              
            </div>
        </form>
    <table className="table">
      <thead>
        <tr>
          <th>ID</th>
          <th>Title</th>
          <th>Price</th>
          <th>Description</th>
          <th>Category</th>
          <th>Image</th>
          <th>Rating</th>
        </tr>
      </thead>
      <tbody>
   {data()}
      </tbody>
    </table>
   
  
  </div>
)
}
