import React, { useState } from "react";

export default function Test11() {
  const [formvalue, setformvalue] = useState({
    firstname: "",
    lastename: "",
    ans: "",
    checkbox: false
  });

  const [submittedData, setSubmittedData] = useState(null);

  const displaysubmit = (e) => {
    const currentTarget = e.currentTarget;
    const id = currentTarget.id;
    let value = undefined;

    switch (currentTarget.type) {
      case "text":
        value = currentTarget.value;
        break;
      case "checkbox":
        value = currentTarget.checked;
        break;
      default:
        break;
    }

    setformvalue((prevState) => {
      return { ...prevState, [id]: value };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmittedData(formvalue);
  };

  return (
    <>
      <div className="form-group">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="firstname">Firstname</label>
            <input
              type="text"
              name="firstname"
              id="firstname"
              value={formvalue.firstname}
              onChange={displaysubmit}
            />
          </div>

          <div className="form-group">
            <label htmlFor="lastename">Lastename</label>
            <input
              type="text"
              name="lastename"
              id="lastename"
              value={formvalue.lastename}
              onChange={displaysubmit}
            />
          </div>

          <div className="form-group">
            <label htmlFor="ans">Ans</label>
            <input
              type="text"
              name="ans"
              id="ans"
              value={formvalue.ans}
              onChange={displaysubmit}
            />
          </div>

          <div className="form-group">
            <input
              type="checkbox"
              name="checkbox"
              id="checkbox"
              checked={formvalue.checkbox}
              onChange={displaysubmit}
            />
            <label htmlFor="checkbox">Checkbox</label>
          </div>

          <button type="submit">Click</button>
        </form>
      </div>

      {submittedData && (
        <table className="table">
          <thead>
            <tr>
              <th>Firstname</th>
              <th>Lastename</th>
              <th>Ans</th> const [submittedData, setSubmittedData] = useState(null);
              <th>Checkbox</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{submittedData.firstname}</td>
              <td>{submittedData.lastename}</td>
              <td>{submittedData.ans}</td>
              <td>{submittedData.checkbox ? "Checked" : "Unchecked"}</td>
            </tr>
          </tbody>
        </table>
      )}
    </>
  );
}
