import React, { useContext, useEffect, useRef } from "react";
import { UsersContext } from "./apk";

export default function Create({ lastid = 0, onaddusers }) {
    const fullname = useRef();
    const country = useRef();

    const context = useContext(UsersContext);

    useEffect(() => {
        console.log("testing de context", context);
    }, [context]);

    const handlesubmit = (e) => {
        e.preventDefault();

        const fullnamedata = fullname.current.value;
        const countrydata = country.current.value;
        const iddata = lastid + 1;
        const newUser = { fullname: fullnamedata, country: countrydata, id: iddata };

        onaddusers(newUser);
    };

    return (
        <>
            <form action="" onSubmit={handlesubmit} className="container mt-4">
                <div className="mb-3">
                    <label htmlFor="id" className="form-label">id</label>
                    <input type="text" readOnly name="id" id="id" value={lastid + 1} className="form-control" />
                </div>
                <div className="mb-3">
                    <label htmlFor="fullname" className="form-label">fullname</label>
                    <input type="text" name="fullname" id="fullname" ref={fullname} className="form-control" />
                </div>
                <div className="mb-3">
                    <label htmlFor="country" className="form-label">country</label>
                    <select name="country" id="country" ref={country} className="form-select">
                        <option value="maroc">Maroc</option>
                        <option value="france">France</option>
                        <option value="espagne">Espagne</option>
                        <option value="usa">USA</option>
                    </select>
                </div>
                <button type="submit" className="btn btn-primary">submit</button>
            </form>
        </>
    );
}
