import { useParams } from "react-router-dom";
import { UsersContext } from "./apk"; // Assurez-vous d'importer correctement le contexte

export default function Delete() {
  const { id } = useParams();
  const context = useContext(UsersContext);  // Utilisation du contexte
  const [deleteuser, setDeleteUser] = useState(null);

  useEffect(() => {
    // Chercher l'utilisateur par ID dans le contexte
    const user = context.usersd.find((user) => user.id === parseInt(id));

    if (user) {
      setDeleteUser(user);
      
    } else {
      console.log("User not found");
    }
  }, [id, context]);

