import React, { useMemo } from "react";

// Fonction qui simule un calcul coûteux
const expensivecalcul = (value) => {
    console.log("Calcul en cours...");
    for (let i = 0; i < 10000; i++) {
        value++;
    }
    return value; // Retourne la valeur finale après les calculs
};

export default function UseMemo() {
    // Fonction appelée au clic
    const disblayappel = () => {
        const n = 1; // Valeur initiale
        const dateinit = performance.now(); // Heure de début du calcul
        
        // Fonction qui exécute le calcul et mesure le temps
        const caluculer = useMemo(() => {
            const value = expensivecalcul(n); // Exécute le calcul
            const findate = performance.now(); // Heure de fin
            alert(`Le calcul a pris ${(findate - dateinit).toFixed(2)} ms et la valeur finale est ${value}`);
        },[n])

        caluculer(); // Appel de la fonction caluculer
    };

    return (
        <>
            <input type="submit" value="Click" onClick={disblayappel} />
        </>
    );
}
