import React from "react";


export const initial_value={
    
       error:undefined,
       loading:true,
       productlist:[]

  
};

export const product_reducer=(state,action)=>{
    switch(action.type){
     case 'ERROR_CHANGE':

       return{
        ...state ,
    error:action.payload.haserror

    
       }
      
       case 'LOADING_CHANGE':

       return{
        ...state ,
    error:action.payload.isloading

    
       }

       case 'PRODUCTLIST_CHANGE':

       return{
        ...state ,
    error:action.payload.collection

    
       }
   

    default:
        return state
   }


}
