    console.log("affichage", data)
}

const handlesubmit = (e) => {
    e.preventDefault();
   
    const fullnamedata = fullname.current.value;
    const countrydata = country.current.value;
    setdata(<div styleName={styles.prevent}></div>,{fullname:fullnamedata});
    setdata(...prevent,{fullname:countrydata});
    console.log("affichage", fullnamedata,countrydata);
    handleform()
    
};

    return(
        <>
        <form action="">
            <label htmlFor="">id</label>???
            <label htmlFor="">fullname</label>
            <input type="text" name="fullname" id="fullname" ref={fullname} />
            <select name="country" id="country" ref={country}>
                <option value="maroc">maroc</option>
                <option value="frence">frence</option>
                <option value="espane">espane</option>
                <option value="usa">usa</option>
            </select>
            
            <button type="submit" onClick={handlesubmit}>submit</button>
        </form>
        </>
    )
}
