import React from 'react';
import { motion } from 'framer-motion';

export default function HeroBanner() {
    return (
        <motion.div
            initial={{ x: -350 }} // Départ hors de l'écran à gauche
            animate={{ x: 0 }} // Se déplace à la position d'origine
            transition={{
                duration: 1.5,
                ease: 'easeInOut',
            }}
            style={{
                textAlign: 'center',
                padding: '50px 20px',
                backgroundColor: '#f9f9f9',
                borderRadius: '15px',
                boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
            }}
        >
            <motion.h2
                className="display-5 fw-normal"
                initial={{
                    opacity: 0,
                    y: -50,
                }}
                animate={{
                    opacity: 1,
                    y: 0,
                    scale: 1.2,
                    color: 'darkgoldenrod',
                    textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
                }}
                transition={{
                    duration: 1,
                    delay: 0.5,
                }}
                style={{ marginBottom: '20px' }}
            >
                Bienvenue to Website
            </motion.h2>

            <motion.p
                initial={{
                    opacity: 0,
                    scale: 0.8,
                }}
                animate={{
                    opacity: 1,
                    scale: 1,
                }}
                transition={{
                    duration: 1,
                    delay: 1,
                }}
                style={{
                    fontSize: '1.2rem',
                    lineHeight: '1.6',
                    color: '#333',
                }}
            >
                Luffy, le héros de One Piece, possède des pouvoirs élastiques grâce au Fruit Gomu Gomu no Mi. <br />
                Ses mouvements reflètent son énergie débordante et sa détermination inébranlable.
            </motion.p>
        </motion.div>
    );
}
