import React, { useRef, useState } from "react";

export default function Test14(){

    const name=useRef()
    const email=useRef()
    const message=useRef()
    const country = useRef()
    const checkbox=useRef()
const [error , seterror]=useState([])
const formvalide=(e)=>{
e.preventDefault()

const namevalue= name.current.value;
 const emailvalue =   email.current.value;
  const messagevalue =  message.current.value;
  const countryvalue=  country.current.value;
  const checkboxvalue= checkbox.current.cheked;
        let formvalid=true
const newerror =[]
           // Validation
    if (namevalue.trim() === "") {
      
        seterror(preventstate=>{
            return [preventstate ,"name est requird"]
        })
        formvalid=false
      }


      if (emailvalue.trim() === "") {
      
        seterror(preventstate=>{
            return [preventstate ,"email est requird"]
        })
        formvalid=false
      }

      if (messagevalue.trim() === "") {
      
        seterror(preventstate=>{
            return [preventstate ,"message est requird"]
        }) 
        formvalid=false
      }

      if (countryvalue === "") {
      
       
            return newerror.push("country required")
            formvalid=false
      }


      if (!checkboxvalue) {
      
        return newerror.push("checkbox required")
        formvalid=false
      }
      
      seterror(newerror)
      
      console.log('ccccck',error.toString())

}
    return(
        <div class="form-group">

          {
            error.length>0?<div className="alert alert-danger" role="alert"><ul>{error.map((errors,key)=><li key={key}>{errors}</li>)}</ul></div>:""
          }
            <form action="" onSubmit={formvalide}>
        <input type="text" name="name" id="name" ref={name}/><br />
        <input type="email" name="email" id="email" ref={email} /><br />
        <textarea  name="message" id="message" ref={message}/><br />
     <select name="country" id="country" ref={country}><br />
        <option value="Maroc" >Maroc</option>
        <option value="Algery">Algery</option>
        <option value="egybest">egybest</option>
     </select>
        

        <input type="checkbox" name="checkbox" id="checkbox" ref={checkbox}/><br />
        <input type="submit" name="submit" />
        </form>
        </div>
    )
}
