import { createSlice } from "@reduxjs/toolkit";

// Define initial state
const initialState = {
  name: 'ali',
  country: 'maroc',
  age: 21
};

// Create user slice
const UserSlice = createSlice({
  name: 'user',
  initialState, // Use correct casing here
  reducers: {
    RestUser(state) {
      // Reset user state logic (optional)
      return initialState; // Resetting to the initial state, if needed.
    },
    UpdateUser(state, action) {
      const { name, country, age } = action.payload;
      // Update user fields based on payload values provided in action
      if (name !== undefined) state.name = name;
      if (country !== undefined) state.country = country;
      if (age !== undefined) state.age = age;
    }
  }
});

// Export actions and reducer
export const { RestUser, UpdateUser } = UserSlice.actions; 
export default UserSlice.reducer;
