import React, { useEffect, useState } from "react";

export default function Test9() {
  const [count, setCount] = useState(new Date());

  useEffect(() => {
    const intervalId = setInterval(() => {
      console.log("componentDidUpdate");
      setCount(new Date());
    }, 900);

    // Nettoyage de l'intervalle lors du démontage du composant
    return () => clearInterval(intervalId);
  }, []); // Le tableau de dépendances est vide pour ne pas recréer l'intervalle à chaque mise à jour

  const addMinute = () => {
    setCount(prevState => {
      const newDate = new Date(prevState);
      newDate.setMinutes(newDate.getMinutes() + 1); // Ajout d'une minute
      return newDate;
    });
  };

  return (
    <>
      {count.toString()}
      <button onClick={addMinute}>
        Click (Add 1 Minute)
      </button>
    </>
  );
}
