import React, { useEffect, useState } from "react";
import { UseFetch } from "./useFetch";
import Production from "../partie1/p10/production";

export default function CustomHooks() {
  const [productList, setProductList] = useState([]);
  const [searchInput, setSearchInput] = useState("");
  const [categoryList, setCategoryList] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");

  const { data: products, error: productError } = UseFetch("https://fakestoreapi.com/products");
  const { data: categories, error: categoryError } = UseFetch("https://fakestoreapi.com/products/categories");

  useEffect(() => {
    if (products) setProductList(products);
    if (categories) setCategoryList(categories);
  }, [products, categories]);

  const displayCategories = () => {
    if (!categoryList.length) return null;

    return categoryList.map((category, index) => (
      <button
        key={index}
        className="btn btn-secondary m-2"
        onClick={() => setSelectedCategory(category)}
      >
        {category}
      </button>
    ));
  };

  const filteredData = () => {
    return productList
      .filter((product) => {
        const matchesSearch =
          product.id.toString().startsWith(searchInput) ||
          product.title.toLowerCase().includes(searchInput.toLowerCase()) ||
          product.price.toString().startsWith(searchInput) ||
          product.description.toLowerCase().includes(searchInput.toLowerCase()) ||
          product.category.toLowerCase().startsWith(searchInput.toLowerCase());

        const matchesCategory =
          selectedCategory === "" || product.category === selectedCategory;

        return matchesSearch && matchesCategory;
      })
      .map((product, index) => (
        <Production productData={product} key={index} />
      ));
  };

  const handleSearch = (e) => {
    e.preventDefault();
    setSearchInput(document.querySelector("#search").value);
  };

  if (productError || categoryError) {
    return <p>Error fetching data: {productError || categoryError}</p>;
  }

  return (
    <div className="container-fluid ms-auto w-75">
      <h2>Search</h2>
      <form onSubmit={handleSearch}>
        <div className="form-group">
          <label htmlFor="search">Search:</label>
          <input type="text" name="search" id="search" className="form-control" />
          <button type="submit" className="btn btn-primary mt-2">Search</button>
          <hr />
          <h5 className="title">Category:</h5>
          <div className="btn-group">{displayCategories()}</div>
        </div>
      </form>
      <table className="table mt-4">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Price</th>
            <th>Description</th>
            <th>Category</th>
            <th>Image</th>
            <th>Rating</th>
          </tr>
        </thead>
        <tbody>{filteredData()}</tbody>
      </table>
    </div>
  );
}
