import React, { useEffect, useReducer, useState } from "react";
import Production from "./production";
import { initial_value, product_reducer } from "./productreducer";

export default function Test18() {
  const [searchinput, setsearchinput] = useState("");
  const [categoryliste, setcategoryliste] = useState([]);
  const [getcategoryliste, setgetcategoryliste] = useState("");
  const [productvalue, dispatch] = useReducer(product_reducer, initial_value);

  // Récupérer les produits
  const product = () => {
    dispatch({ type: "LOADING_CHANGE", payload: { isloading: true } });
    fetch("https://fakestoreapi.com/products")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((response) => {
        console.log("Products fetched:", response); // Debug
        dispatch({ type: "PRODUCTLIST_CHANGE", payload: { collection: response } });
      })
      .catch((error) => {
        console.error("Error fetching products:", error.message); // Debug
        dispatch({ type: "ERROR_CHANGE", payload: { haserror: error.message } });
      })
      .finally(() => {
        dispatch({ type: "LOADING_CHANGE", payload: { isloading: false } });
      });
  };

  // Récupérer les catégories
  const getcategory = () => {
    fetch("https://fakestoreapi.com/products/categories")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((categories) => {
        console.log("Categories fetched:", categories); // Debug
        setcategoryliste(categories);
      })
      .catch((error) => {
        console.error("Error fetching categories:", error.message); // Debug
      });
  };

  // Chargement initial des données
  useEffect(() => {
    console.log("Fetching products and categories...");
    product();
    getcategory();
  }, []);

  // Afficher les boutons des catégories
  const displaycategorys = () => {
    return categoryliste.map((category) => (
      <button
        className="btn btn-secondary"
        name="category"
        id="category"
        onClick={() => setgetcategoryliste(category)}
        key={category}
      >
        {category}
      </button>
    ));
  };

  // Filtrer et afficher les produits
  const data = () => {
    const filteredProducts = productvalue.productlist.filter(
      (product) =>
        (product.id.toString().startsWith(searchinput) ||
          product.title.toString().includes(searchinput) ||
          product.price.toString().startsWith(searchinput) ||
          product.description.toString().includes(searchinput) ||
          product.category.toString().startsWith(searchinput)) &&
        product.category.toString().includes(getcategoryliste)
    );

    return filteredProducts.map((product, key) => (
      <Production productdata={product} key={key} />
    ));
  };

  // Gestion de la recherche
  const search = (e) => {
    e.preventDefault();
    const searchdata = document.querySelector("#search").value;
    setsearchinput(searchdata);
  };

  if (productvalue.isloading) {
    return <div>Loading...</div>; // Affichage de chargement
  }

  if (productvalue.error) {
    return <div>Error: {productvalue.error}</div>; // Affichage des erreurs
  }

  return (
    <div className="container-fluid ms-auto w-75">
      <h2>Search</h2>
      <form onSubmit={search}>
        <div className="form-group">
          <label htmlFor="search">Search</label>
          <input type="text" name="search" id="search" />
          <input type="submit" name="submit" id="submit" />
          <hr />
          <h5 className="title">Category:</h5>
          <div className="btn-group">{displaycategorys()}</div>
        </div>
      </form>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Price</th>
            <th>Description</th>
            <th>Category</th>
            <th>Image</th>
            <th>Rating</th>
          </tr>
        </thead>
        <tbody>{data()}</tbody>
      </table>
    </div>
  );
}
