import React from "react";
import { useSelector } from "react-redux";
import { UsersSelectors } from "./selectors/UsersSelectors";

const UserList = () => {
    const users = useSelector(UsersSelectors);

    if (!Array.isArray(users)) {
        console.error("Expected users to be an array", users);
        return <p>Erreur lors du chargement des utilisateurs.</p>;
    }

    return (
        <div>
            <h1>Liste des Utilisateurs</h1>
            {users.length > 0 ? (
                <ul>
                    {users.map(user => (
                        <li key={user.id}>{user.name}</li> // Assurez-vous que 'id' et 'name' existent sur chaque objet utilisateur.
                    ))}
                </ul>
            ) : (
                <p>Aucun utilisateur trouv√©.</p>
            )}
        </div>
    );
};

export default UserList;
