import React, { useEffect, useState } from "react";
import Production from "./production";


export default function Test10(){

    const [productliste  , setproductliste]=useState([])
    const [searchinput  , setsearchinput]=useState("")
    const [categoryliste  , setcategoryliste]=useState("")
    
  const  product=()=>{
    fetch("https://fakestoreapi.com/products")
    .then((response) => response.json()) 
        .then((response)=>setproductliste(response)  )

    }
    const getcategory=()=>{
        fetch("https://fakestoreapi.com/products/categories")
        .then((response) => response.json()) 
        .then((response)=>setcategoryliste(response)  )
        
    }
    useEffect(()=>{
        product()
        getcategory()
      
    },[])        
    
    const displaycategorys=()=>{
        console.log("dddddd" , categoryliste)
        if(categoryliste){
            return  categoryliste.map((category)=>{
            return  <button className="btn btn-secondary" name="category" id="category"   >
                {category}
                
              </button>
            })
        }
        
       
    }
    
    const data=()=>{
        const categorys = document.querySelector("#category").value
        console.log("nnnnnn" ,categorys )
       
  
 console.log('gggg' , categoryliste)
    const filteredProducts = productliste.filter((product) =>
        product.id.toString().startsWith(searchinput)
    ||product.title.toString().includes(searchinput)
    ||product.price.toString().startsWith(searchinput)
    ||product.description.toString().includes(searchinput)
    ||product.category.toString().startsWith(searchinput)
    ||  product.category.toString().startsWith(categoryliste)

   
    )
   
    
       return filteredProducts.map(   (preventstate,key) =>{
          
           return <Production productdata={preventstate} key={key}/>

        })
    }

    
    const search =(e)=>{
        e.preventDefault();
const searchdata= document.querySelector("#search").value
setsearchinput(searchdata)

    }
    return(
        <div className="container-fluid ms-auto w-75">
            <h2>search</h2>
            <form onSubmit={search}>
                <div class="form-group">
                  <label for=""></label>
                 <input type="text" name="search" id="search" />
                  <hr />
                  <h5 className="title"> category :</h5>
                <div className="btn group">
               
                     {displaycategorys()}
               
                </div>
                  
                </div>
            </form>
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Price</th>
              <th>Description</th>
              <th>Category</th>
              <th>Image</th>
              <th>Rating</th>
            </tr>
          </thead>
          <tbody>
       {data()}
          </tbody>
        </table>
       
      
      </div>
    )
}
