import React, { createContext, useState } from "react";
import Read from "./read";
import Create from "./create";
import Layout from "./route_layout";
export const  UsersContext=createContext({
    usersd:[],
    lastid:0
})

export default function App() {
  const [usersd, setusersd] = useState([]);
  const [lastid, setlastid] = useState(0);

  const onaddusers = (newUser) => {
 
    setlastid(newUser.id)
    setusersd([...usersd, newUser])
  
  };
  const updateuser = (changeUser) => {
    const   {id , ...rest}=changeUser
    console.log("testing id de update" , rest)
    // Debugging
    setusersd(usersd=>
        usersd.map((user) => user.id == id ? {...rest ,id :user.id }  : user, console.log('not trouve data')
      )
    );
  };
  const deleteuser = (id) => {
    console.log("test id de delete in app", )
    setusersd((usersd) => usersd.filter((user) => user.id !== id ));
  };

  return (
    
      <>
    
    <UsersContext.Provider value={{usersd : usersd ,lastid:lastid ,onaddusers:onaddusers ,updateuser:updateuser ,deleteuser:deleteuser}}>
      <Layout/>
    </UsersContext.Provider>
      
     
      
    </>
  );
}
