import React, { useMemo, useState } from "react";

// Fonction qui simule un calcul coûteux
const expensiveCalcul = (value) => {
    console.log("Calcul en cours...");
    for (let i = 0; i < 10000; i++) {
        value++;
    }
    return value; // Retourne la valeur finale après les calculs
};

export default function UseMemoComponent() {
    const [inputValue, setInputValue] = useState(1); // Valeur initiale

    // Mémorisation du calcul coûteux
    const calculatedValue = useMemo(() => {
        const startTime = performance.now();
        const result = expensiveCalcul(inputValue);
        const endTime = performance.now();
        console.log(`Le calcul a pris ${(endTime - startTime).toFixed(2)} ms`);
        return result;
    }, [inputValue]); // Recalculé uniquement si inputValue change

    const handleClick = () => {
        alert(`La valeur calculée est ${calculatedValue}`);
    };

    return (
        <>
            <input
                type="number"
                value={inputValue}
                onChange={(e) => setInputValue(Number(e.target.value))}
            />
            <button onClick={handleClick}>Click</button>
        </>
    );
}
