import React from "react";


export const form_reducer_default_value={
    
        firstname:"",
        lastename:"",
        ans:"" ,
        checkbox:false

  
};

export const form_reducer=(state,action)=>{
    switch(action.type){
     case 'INPUT_CHANGE':

       return{
        ...state ,
        [action.payload.name]:action.payload.value
       }
      
      
        
        case 'INPUT_CHANINPUT_checkboxINPUT_checkboxGE':
           return{
        ...state ,
        [action.payload.name]:action.payload.value
       }
      
      
        

    default:
        return state
   }


}
