import React, { useEffect, useRef, useState } from "react";


export default function Create({lastid,onaddusers}){

const fullname=useRef()
const country= useRef()

const [data,setdata]=useState([])


const handlesubmit = (e) => {
    e.preventDefault();
   
    const fullnamedata = fullname.current.value;
    const countrydata = country.current.value;
    const iddata = lastid + 1;
    const newUser = { fullname: fullnamedata, country: countrydata, id: iddata, };
    setdata((prev) => ([
        ...prev, newUser
    ]));

    onaddusers((prev) => [...prev,newUser]);
  
};

    return(
        <>
        <form action="" onSubmit={handlesubmit}>
            <label htmlFor="">id</label>
             <input type="text" readOnly name="" id="" value={lastid + 1} />
            <label htmlFor="">fullname</label>
            <input type="text" name="fullname" id="fullname" ref={fullname}  />
            <select name="country" id="country" ref={country}>
                <option value="maroc">maroc</option>
                <option value="frence">frence</option>
                <option value="espane">espane</option>
                <option value="usa">usa</option>
            </select>
            
            <button type="submit" >submit</button>
        </form>
        </>
    )
}
