import React, { useEffect, useRef, useState } from "react";


export default function Create({lastid,onaddusers}){

const fullname=useRef()
const country= useRef()

const [data,setdata]=useState([])
const handleform=()=>{
    if (data.length > 0) {
     
        onaddusers(data);
    } else {
        console.log("Affichage: no data");
    }
    
}

const handlesubmit = (e) => {
    e.preventDefault();
  
    const fullnamedata = fullname.current.value;
    const countrydata = country.current.value;
    const iddata = lastid + 1;
    const newUser = { fullname: fullnamedata, country: countrydata, id: iddata };
  
    setdata((prev) => [...prev, newUser]);
    onaddusers([newUser]); // Utiliser directement l'utilisateur nouvellement créé
  };
  
    return(
        <>
          <form onSubmit={handlesubmit}>
        <label htmlFor="id">ID</label>
        <input type="text" readOnly value={lastid + 1} />
        <label htmlFor="fullname">Fullname</label>
        <input type="text" id="fullname" ref={fullname} required />
        <label htmlFor="country">Country</label>
        <select id="country" ref={country} defaultValue="maroc">
          <option value="maroc">Maroc</option>
          <option value="frence">France</option>
          <option value="espane">Espagne</option>
          <option value="usa">USA</option>
        </select>
        <button type="submit">Submit</button>
      </form>
        </>
    )
}
